package ncd.cliimport inform.dist.DistanceCalculatorimport matrix.store.TermMatrixReadOnlyif ((args.length) < 1) {    println "usage ${this.class} matrix-location"    return}matrix_dir = args[0]matrix_dir = matrix_dir.replace("~", System.properties['user.home'])def mtx = new TermMatrixReadOnly(new File(matrix_dir))String[] terms = mtx.termsfor (term in terms) {    System.out.println(term)}